INSERT INTO `thesis_tables` VALUES (1,'tbl-2-1','literature_review','Comparison of Existing SSH Security Solutions','<table class=\"thesis-table\"><thead><tr><th>Solution</th><th>Detection Method</th><th>ML Support</th><th>SME Focus</th></tr></thead><tbody><tr><td>Fail2ban</td><td>Rule-based</td><td>No</td><td>Yes</td></tr><tr><td>OSSEC</td><td>Rule-based</td><td>Limited</td><td>No</td></tr><tr><td>SSH Guardian</td><td>Hybrid</td><td>Yes</td><td>Yes</td></tr></tbody></table>',1,'2025-12-24 00:04:28'),(2,'tbl-3-1','methodology','Feature Engineering Parameters','<table class=\"thesis-table\"><thead><tr><th>Feature</th><th>Type</th><th>Description</th></tr></thead><tbody><tr><td>IP Reputation</td><td>Numeric</td><td>AbuseIPDB confidence score</td></tr><tr><td>Geo Anomaly</td><td>Boolean</td><td>Login from unusual location</td></tr><tr><td>Time Pattern</td><td>Categorical</td><td>Off-hours vs business hours</td></tr></tbody></table>',1,'2025-12-24 00:04:28'),(3,'tbl-4-1','implementation','Technology Stack','<table class=\"thesis-table\"><thead><tr><th>Component</th><th>Technology</th><th>Purpose</th></tr></thead><tbody><tr><td>Backend</td><td>Flask/Python</td><td>API Server</td></tr><tr><td>Frontend</td><td>React/Next.js</td><td>Dashboard</td></tr><tr><td>Database</td><td>MySQL 8.0</td><td>Data Storage</td></tr><tr><td>ML</td><td>LightGBM</td><td>Threat Detection</td></tr></tbody></table>',1,'2025-12-24 00:04:28'),(4,'tbl-5-1','evaluation','Model Performance Comparison','<table class=\"thesis-table\"><thead><tr><th>Model</th><th>Accuracy</th><th>Precision</th><th>Recall</th><th>F1-Score</th></tr></thead><tbody><tr><td>Random Forest</td><td>95.12%</td><td>94.34%</td><td>95.89%</td><td>95.11%</td></tr><tr><td>XGBoost</td><td>95.67%</td><td>94.89%</td><td>96.45%</td><td>95.66%</td></tr><tr><td>LightGBM</td><td>96.78%</td><td>96.01%</td><td>97.56%</td><td>96.78%</td></tr></tbody></table>',1,'2025-12-24 00:04:28'),(5,'tbl-5-2','evaluation','Detection Improvement Over Fail2ban','<table class=\"thesis-table\"><thead><tr><th>Metric</th><th>Fail2ban</th><th>SSH Guardian</th><th>Improvement</th></tr></thead><tbody><tr><td>First-attempt Detection</td><td>0%</td><td>45%</td><td>+45%</td></tr><tr><td>Distributed Attack Detection</td><td>Limited</td><td>High</td><td>Significant</td></tr><tr><td>False Positive Rate</td><td>12%</td><td>3.2%</td><td>-73%</td></tr></tbody></table>',2,'2025-12-24 00:04:28');
